import { Component } from '@angular/core';
import { UgandaPlace } from '../uganda';
import { UgandaServiceService } from '../uganda-service.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  places: UgandaPlace[] = [];

  constructor(private ugandaService: UgandaServiceService){}

  ngOnInit(): void{
    this.getPlaces();
  }

  getPlaces(): void{
    this.ugandaService.getPlaces()
                      .subscribe((places)=>(this.places=places.slice(1,5)));
  }

}
