import { Component } from '@angular/core';
import { UgandaPlace } from '../uganda';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';
import { UgandaServiceService } from '../uganda-service.service';

@Component({
  selector: 'app-uganda-place-detail',
  templateUrl: './uganda-place-detail.component.html',
  styleUrls: ['./uganda-place-detail.component.css']
})
export class UgandaPlaceDetailComponent {
  place: UgandaPlace | undefined;

  constructor(
    private route: ActivatedRoute,
    private ugandaService: UgandaServiceService,
    private location: Location
  ){}

  ngOnInit(): void{
    this.getPlace();
  }

  getPlace(): void{
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.ugandaService.getPlace(id).subscribe((place)=>(this.place = place));
  }

  goBack(): void{
    this.location.back();
  }

  save(): void{
    if (this.place) {
      this.ugandaService.updatePlace(this.place).subscribe(() =>this.goBack());

    }
  }

}
