import { Component } from '@angular/core';
import { UgandaPlace } from '../uganda';
import { UgandaServiceService } from '../uganda-service.service';

@Component({
  selector: 'app-uganda-places',
  templateUrl: './uganda-places.component.html',
  styleUrls: ['./uganda-places.component.css']
})
export class UgandaPlacesComponent {

  places: UgandaPlace[] = [];

  constructor(private ugandaService: UgandaServiceService){}


  ngOnInit() : void{
    this.getPlaces();
  }

  getPlaces(): void{
    this.ugandaService.getPlaces().subscribe((places)=>(this.places = places));
  }

  add(name: string): void {
    name = name.trim();
    if (!name) {
      return;

    }
    this.ugandaService.
          addPlace({ name} as UgandaPlace).subscribe((place)=>this.places.push(place));
  }

  delete(place: UgandaPlace): void {
    this.places = this.places.filter((p) => p!== place);
    this.ugandaService.deletePlace(place.id).subscribe();

  }


  deleteAll(): void{
    this.ugandaService.deleteAllPlaces().subscribe();
  }



}
<h2>Places to visit in Uganda</h2>
<div>
    <label for="place-name">
        Place name: <input id="new-place" #placeName />
    </label>
    <button type="button" (click)="add(placeName.value); placeName.value = ''">
        Add place to visit
    </button>
</div>
<ul class="places">
    <li *ng>

    </li>
</ul>
