import { Component, OnInit } from '@angular/core';
import { Observable, Subject, debounceTime, distinctUntilChanged, switchMap } from 'rxjs';
import { UgandaPlace } from '../uganda';
import { UgandaServiceService } from '../uganda-service.service';

@Component({
  selector: 'app-uganda-place-search',
  templateUrl: './uganda-place-search.component.html',
  styleUrls: ['./uganda-place-search.component.css']
})
export class UgandaPlaceSearchComponent implements OnInit {
  places$!: Observable<UgandaPlace[]>;

  private searchTerms = new Subject<string>();

  constructor(private ugandaService: UgandaServiceService){}

  search(term: string): void{
    this.searchTerms.next(term);
  }

  ngOnInit(): void {
    this.places$ = this.searchTerms.pipe(
      debounceTime(300),

      distinctUntilChanged(),

      switchMap((term: string)=>this.ugandaService.searchPlaces(term))
    );
  }

}
